mapper:

    select * from dust_board

        where id in (select id from dust_member where nickname like concat(‘%’, #{word}, ‘%’))


        where title like concat(‘%’, #{word}, ‘%’)


        where content like concat(‘%’, #{word}, ‘%’)




    select * from dust_board where board_no = #{boardNo}



    update dust_board set title = #{title}, content = #{content} where board_no = #{boardNo}



    delete from dust_board where board_no = #{boardNo}



//동우 내가 쓴 게시물 글 조회

    SELECT board_no, title, content, id, written_date, view_count
    FROM dust_board
    WHERE id=#{id}
    ORDER BY board_no


//동우 내가 쓴 댓글 조회

    SELECT comment_num, content, id, written_date, view_count
    From dust_comment
    Where id=#{id}
    ORDER BY comment_num
